class Solution {
public: 
    int numberOfAlternatingGroups(vector<int>& colors, int k) { 
        int n = colors.size(); 
        int cnt = 0; 

        if (k == 1) { 
            return n; 
        } 

        int al = 1; 
        int i = 1; 
        while (i < n + k - 1) { 
            int curr = i; 
            int prev = i - 1; 

            if (curr >= n) { 
                curr -= n; 
            } 
            if (prev >= n) { 
                prev -= n; 
            } 

            if (colors[curr] != colors[prev]) { 
                ++al; 
            } else { 
                al = 1; 
            } 

            if (al >= k) { 
                ++cnt; 
            } 

            ++i; 
        } 

        return cnt; 
    } 
};
