class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        vector<int> v;
        v=colors;

        for(auto el: colors)v.push_back(el);

        int ans=0;

        int j=0;

        int i=1;

        while(j<colors.size()&&i<v.size()){

            if(v[i]==v[i-1]){
                j=i;
            }

            if(i-j+1==k){
                ans++;
                j++;
            }

            i++;
        }

        return ans;

    }
};
