class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int prev = -1;
        int j = 0;
        int ans=0;
        vector<int> arr(c.begin(),c.end());
        for(int i=0;i<k-1;i++){
            arr.push_back(c[i]);
        }
        for(int i=0;i<arr.size();i++){
            if(arr[i]==prev){
                j = i;
            }
            if(i-j+1>=k){
                ans++;
            }
            prev = arr[i];
        }
        return ans;
    }
};
