class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int ans=0,x=0;
        while(x<n)
        {
            int z=1;
            for(int i=0;i<k-1;i++)
            {
                if(colors[(x+i)%n]==colors[(x+i+1)%n])
                {
                    z=0;
                    x=(x+i+1);
                    break;
                }
            }
            if(z==1)
            {
                while(x<n)
                {
                    if(colors[(x+k-1)%n]!=colors[(x+k-2)%n])
                    {
                        ans++;
                        x++;
                    }
                    else
                    {
                        x=(x+k-1);
                        break;
                    }
                }
            }
            else
            {
                continue;
            }
        }
        return ans;
    }
};
