class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size(), ans=0;
        int i=0, j=0, len=0; 
        while(i<n){
            while(i<n && j-i+1<k){
                if(a[j%n]==a[(j+1)%n]){
                    i=j+1;
                    j=i;
                }
                else{
                    j++;
                }
            }
            if(i<n){
                ans++;
                i++;
            }
        }
        return ans;
    }
};
