class Solution {
public:
    int calc(int k,vector<int>&arr)
    {
        vector<int>prev(arr.size(),-1);
        prev[0]=0;
        int count=0;
        for(int i=1;i<arr.size();i++)
        {
            if(arr[i]!=arr[i-1])
            {
                prev[i]=prev[i-1];
            }
            else
            {
                prev[i]=i;
            }
        }
        for(int i=0;i<=(arr.size()-k);i++)
        {
            if(prev[i+k-1]<=i)
            {
                count++;
            }
        }
        return count;
    }
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>circ;
        for(int i=colors.size()-k+1;i<colors.size();i++)
        {
            circ.push_back(colors[i]);
        }
        for(int i=0;i<(k-1);i++)
        {
            circ.push_back(colors[i]);
        }
        return (calc(k,colors)+calc(k,circ));
    }
};
