class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int result = 0;
        if (k == 1) {
            return colors.length;
        }

        int len = 1;
        int i = 1;
        while (i < colors.length + k - 1) {
            int tem = i;
            int temp = i - 1;

            if (tem >= colors.length) {
                tem -= colors.length;
            }
            if (temp >= colors.length) {
                temp -= colors.length;
            }

            if (colors[tem] != colors[temp]) {
                ++len;
            } else {
                len = 1;
            }

            if (len >= k) {
                ++result;
            }

            ++i;
        }
        return result;    
    }
}
