class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int cnt = 0, ans = 0, n= colors.size();
        for(int i=1;i<k-1;i++) {
            if(colors[i]!=colors[i-1]) {
                cnt++;
            }
        }
        for(int i=k-1;i<n+k-1;i++) {
            if(colors[i%n]!=colors[(i-1)%n]) {
                cnt++;
            }
            if(cnt==k-1) {
                ans++;
            }
            if(colors[(i-k+2)%n]!=colors[(i-k+1)%n]) {
                cnt--;
            }
        }
        return ans;
    }
};
