class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();
        for(int i=0;i<min(k-1,n);i++)c.push_back(c[i]);

        int cnt = 1;
        int i = 0;
        int j = 0;
        lli ans =0;
        while(i<n){
            if(j!=(c.size()-1) && c[j+1]!=c[j]){
                cnt++;
            }
            else{
                ans += max(0,cnt-k+1);
                cnt = 1;
                i = j+1;
            }
            j++;
        }

        return ans;
    }
};
