class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i=0;i<k;i++){
            colors.push_back(colors[i]);
        }
        int n=colors.size();
        vector<int>pf(n+1,0);
        for(int i=1;i<n;i++){
            pf[i]+=pf[i-1]+abs(colors[i]-colors[i-1]);
        }
        k--;
        int ans=0;
        for(int i=k+1;i<n;i++){
            if(pf[i]-pf[i-k]==k)ans++;
        }
        return ans;
    }
};
