class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int ans=0,c=0,pv=-1;
        for(int i=0;i<v.size();i++){
            if(v[i]!=pv)c++;
            else c=1;

            if(c==k){
                ans++;
                c--;
            }
            pv=v[i];
        }
        for(int i=0;i<k-1;i++){
            if(v[i]!=pv)c++;
            else c=1;

            if(c==k){
                ans++;
                c--;
            }
            pv=v[i];
        }
        return ans;
    }
};
