class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
    int ib = 0;
    int ie = 1;
    int ans = 0;
    while (ib < n) {
      while (ie < ib + k && colors[ie % n] == 1 - colors[(ie-1)%n]) {
        ie++;
      }
      if (ie == ib + k) {
        ans++;
        ib++;
      } else {
        // 1 0 1 0 0 . .
        // ^       ^
        // ib     ie
        ib = ie;
        ie = ib + 1;
      }
    }
    return ans;
    }
}
