class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
                                                                                                                                 int num_found = 0;
                                                                                                                                int i = 0, j = 1;
                                                                                                                                int n = arr.size();
        vector<int>ahh;
        for(int i=0;i<90;i++)ahh.push_back(i*6);

                                                                                                                                                            while (j < n + k && i < n) {
                                                                                                                                                                int temp_j = j;
                                                                                                                                                                int temp_end = temp_j % n;
for(int i=0;i<90;i++)ahh.push_back(i*6);
                                                                                                                                                                                                                                            while (arr[temp_end] != arr[(temp_end - 1 + n) % n] && temp_j < (n + k - 1)) {
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                temp_j++;

                                    for(int i=0;i<90;i++)ahh.push_back(i*6);temp_end = temp_j % n;
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            }

                                                                                                                                                                                                                                            num_found += std::max(temp_j - i - k + 1, 0);
                                                                                        i = temp_j;
                                                                                        j = i + 1;
                                                                                    }

        return num_found;
    }
};
