class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        queue=deque([],maxlen=k)
        n=len(colors)
        idx=n-1
        res=0
        while idx>=-k+1:
            if not queue or colors[idx]!=queue[-1]:
                queue.append(colors[idx])
            else:
                queue=deque([colors[idx]],maxlen=k)
            idx-=1
            res+=len(queue)==k
        return res
