class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        const int n = colors.size();
        vector<int> v(n);
        for (int i = 0; i < n; ++i) {
            v[i] = (colors[i] != colors[(i + 1) % n] ? 1 : 0);
        }
        int cnt = 0;
        for (int i = 0; i < k - 1; ++i) {
            if (v[i] == 1) {
                ++cnt;
            } else {
                cnt = 0;
            }
        }
        int ans = 0;
        for (int i = 0; i < n; ++i) {
            if (cnt >= k - 1) {
                ++ans;
            }
            int idx = (k - 1 + i) % n;
            if (v[idx] == 1) {
                ++cnt;
            } else {
                cnt = 0;
            }
        }
        return ans;
    }
};
