class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>a;
        int i=0,n=colors.size();
        a=colors;
        for(i=0;i<n;i++)
        {
            a.push_back(colors[i]);
        }
        vector<int>r(4,0);
        for(i=0;i<k;i++)
        {
            if(a[i]==1)
            {
                if(i%2==0)
                    r[2]++;
                else
                    r[3]++;
            }
            else
            {
                if(i%2==0)
                    r[0]++;
                else
                    r[1]++;
            }
        }
        int ans=0;
        for(i=k;i<n+k;i++)
        {
            if(r[0]==0&&r[3]==0&&abs(r[1]-r[2])<=1)
            {
                ans++;
            }
            else if(abs(r[0]-r[3])<=1&&r[1]==0&&r[2]==0)
            {
                ans++;
            }
            if(a[i-k]==1)
            {
                if((i-k)%2==0)
                    r[2]--;
                else
                    r[3]--;
            }
            else
            {
                if((i-k)%2==0)
                    r[0]--;
                else
                    r[1]--;
            }
            if(a[i]==1)
            {
                if(i%2==0)
                    r[2]++;
                else
                    r[3]++;
            }
            else
            {
                if(i%2==0)
                    r[0]++;
                else
                    r[1]++;
            }
        }
        return ans;
    }
};
