from typing import List
class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n=len(colors)
        colors=colors+colors+colors
        ret=0
        right=1
        left=0
        while left<n:
            while right-left<k and colors[right]!=colors[right-1]:
                right+=1
            if right-left==k:
                ret+=1
                left+=1
            else:
                left=right
                right+=1
        return ret
