class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> nc = colors;
        int ans = 0;
        for(int i = 0;i<k-1;i++){
            nc.push_back(colors[i]);
        }
        int start = 0;
        int end = 0;
        for(int i = 1;i<nc.size();i++){
            if(nc[i]==nc[i-1]){
                start = i;
                end = i;
            }
            else{
                end = i;
                if(end-start+1==k){
                    ans+=1;
                    start += 1;
                }
            }
        }
        return ans;
    }
};
