class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> v;
        for(int i=0;i<colors.size();i++) v.push_back(colors[i]);
        for(int i=0;i<k-1;i++) v.push_back(colors[i]);
        int ans=0;
        int n=v.size();
        int start=0,last=v[0],end=0;
        for(int i=1;i<n && end<n;)
        {
            if(end==n) break;
            if(v[i]!=last)
            {
                end++;
                last=v[i];
            }
            else{
                start=end=i;
                last=v[i];
            }
            while(end<n && end-start+1==k)
            {
                ans++;
                start++;
                last=v[end];
                end++;
                if(end==n) break;
                if(v[end]==last)
                {
                    start=end;
                    last=v[end];
                    i=end;
                    break;
                }
                else{
                    last=v[end];
                }
            }
            i++;
            if(end>=n-1) break;
        }
        return ans;
    }
};
