class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& color, int k) {
        vector<int> v;
        for(auto i:color) v.push_back(i); 
        for(auto i:color) v.push_back(i);
        int n=color.size();
        int i=0,j=0;
        int ans=0;
        while(i<n){
            //cout<<i<<" "<<j<<endl;
            if((j-i+1)<k){
                if(v[j]==v[j+1]){
                    j++;
                    i=j;
                }
                else j++;
            }
            else if((j-i+1)==k){
                ans++;
                if(v[j]==v[j+1]){
                    j++;
                    i=j;
                }
                else{
                    i++;
                    j++;
                }
            }
        }
        return ans;
    }
};
