class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& array, int k_value) {
        int size = array.size();
        int count_groups = 0;
        int start_index = 0, end_index = 1;

        while (end_index < size + k_value && start_index < size) {
            int temp_end_index = end_index;
            int temp_final_index = temp_end_index % size;

            while (array[temp_final_index] != array[(temp_final_index - 1 + size) % size] && (temp_end_index < (size + k_value - 1))) {
                temp_end_index++;
                temp_final_index = temp_end_index % size;
            }

            count_groups += max(temp_end_index - start_index - k_value + 1, 0);
            start_index = temp_end_index;
            end_index = start_index + 1;
        }

        return count_groups;
    }
};
