class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        a.insert(a.end(), a.begin(), a.end());
        int ret = 0;
        for (int i = 0; i < n; i++) {
            int j = i + 1;
            while (j < 2 * n && a[j] != a[j-1]) j++;
            if (j - i >= k) ret += min(j - i - k + 1, n - i);
            i = j - 1;
        }
        return ret;
    }
};
