class Solution {
public:

    int rabin_karp(string const& s, string const& t) {
        const int p = 31; 
        const int m = 1e9 + 9;
        int S = s.size(), T = t.size();

        vector<long long> p_pow(max(S, T)); 
        p_pow[0] = 1; 
        for (int i = 1; i < (int)p_pow.size(); i++) 
            p_pow[i] = (p_pow[i-1] * p) % m;

        vector<long long> h(T + 1, 0); 
        for (int i = 0; i < T; i++)
            h[i+1] = (h[i] + (t[i] - 'a' + 1) * p_pow[i]) % m; 
        long long h_s = 0; 
        for (int i = 0; i < S; i++) 
            h_s = (h_s + (s[i] - 'a' + 1) * p_pow[i]) % m; 

        vector<int> occurrences;
        for (int i = 0; i + S - 1 < T; i++) {
            long long cur_h = (h[i+S] + m - h[i]) % m;
            if (cur_h == h_s * p_pow[i] % m)
                occurrences.push_back(i);
        }
        return (int)occurrences.size();
    }

    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i = 0; i < k - 1; i ++) 
            colors.push_back(colors[i]);
        int n = colors.size();
        string s = "";
        for(int i = 0; i < n; i ++) {
            if(colors[i] == 0) s.push_back('a');
            else s.push_back('b');
        }
        string t1 = "", t2 = "";
        char ch = 'a';
        for(int i = 0; i < k; i ++) {
            t1.push_back(ch);
            if(ch == 'a') ch = 'b';
            else ch = 'a';
        }
        ch = 'b';
        for(int i = 0; i < k; i ++) {
            t2.push_back(ch);
            if(ch == 'a') ch = 'b';
            else ch = 'a';
        }
        int ans1 = rabin_karp(t1, s);
        int ans2 = rabin_karp(t2, s);
        return (ans1 + ans2);
    }
};
