class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        var c = new int[colors.length * 2];
        for (int i = 0; i < colors.length; i++) {
            c[i] = c[colors.length + i] = colors[i];
        }

        int res = 0;

        int count = 1;
        for (int i = 1; i < colors.length + k - 1; i++) {
            if (c[i - 1] == c[i]) {
                count = 1;
            } else {
                if (++count >= k) {
                    res++;
                }
            }
        }

        return res;
    }
}
