#define fin for(int i=0;i<n;i++)
#define fjk for(int j=0;j<k-1;j++)
class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        int n=colors.size();
        vector<int>nc(n+k-1);
        fin{
            nc[i]=colors[i];
        }
        fjk{
            nc[n+j]=colors[j];
        }
        int res=0;
        int m=1;
        for(int i=1;i<nc.size();i++){
            if(nc[i]!=nc[i-1]){
                m++;
            }
            else{
                m=1;
            }
            if(m>=k){
                res++;
            }
        }
        return res;
    }
};
