class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> arr = colors;
        for(int i = 0; i < k - 1; i++){
            arr.push_back(colors[i]);
        }
        vector<int> prefix;
        for(int i = 0; i < arr.size() - 1; i++){
            prefix.push_back(arr[i]^arr[i + 1]);
        }
        int sum = 0;
        for(int i = 0; i < k - 1; i++){
            if(prefix[i] == 1)sum++;
        }
        int ans = 0;
        int i = 0, j = k - 1;
        while(j < prefix.size()){
            if(sum == k - 1)ans++;
            cout << sum;
            sum -= prefix[i];
            sum += prefix[j];
            i++;
            j++;

        }
        if(sum == k - 1)ans++;
        cout <<endl;
        return ans;
    }
};
