class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int[] arr = new int[2*n];

        for(int i = 0 ; i < n ; i++) arr[i] = colors[i];
        for(int i = n , xx = 0 ; i < arr.length ; i++ , xx++) arr[i] = colors[xx]; 

        int[] end = new int[n];

        for(int i = 0 ; i < n ; i++){
            int find = arr[i];
            int t = i;

            while(t < arr.length && arr[t] == find){
                if(find == 0) find = 1;
                else find = 0;
                t++;
            }

            for(int j = i ; j < Math.min(n , t) ; j++){
                end[j] = t-1;
            }
            i = t - 1;
        }

        int ans = 0;
        for(int i = 0 ; i < n ; i++){
            int x = end[i];

            int dif = x - i + 1;
            if(dif >= k){
                ans++;
            }
        }
        return ans;
    }
}
