class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans=0;
        int last=0;
        vector <int> v = colors;
        v.insert(v.end(),colors.begin(),colors.end());
        for(int i=1; i < colors.size()+k; i++){
            // cout << v[i] << " ";
            if(v[i]==v[i-1]){
                ans+=max(0,i-last-k+1);
                last=i;
                // cout << i << endl;
            }
        }
        ans+=max(0,(int)colors.size()+k-1-last-k+1);
        return ans;
    }
};
