class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n=v.size();
        for(int i=0;i<k;i++){
            v.push_back(v[i]);
        }
        int p=v.size();
        vector<int>ans(p);
        for(int i=p-1;i>=0;i--){
            if(i==p-1||v[i]==v[i+1]){
                ans[i]=1;
                continue;
            }
            if(v[i]!=v[i+1]){
                ans[i]=1+ans[i+1];
            }
        }
        int c=0;
        for(int i=0;i<n;i++){
            if(ans[i]>=k){
                c++;
            }
        }
        return c;
    }
};
