class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int c=0;
        int j=0,i=0;
        while(j<(n+k-1)){
            if(j-i+1==k) c++,i++;
            if(colors[j%n]==colors[(j+1)%n]){
                i=j+1;
            }
            j++;
        }
        return c;
    }
};
