class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        for i in range(k):
            colors.append(colors[i])

        l = 0
        r = 1
        count = 0

        while l < n and r < n + k:
            if colors[r] != 1 - colors[r - 1]:
                l = r
                r += 1
            else:
                if r - l + 1 == k:
                    count += 1
                    if colors[l] != 1 - colors[l + 1] or colors[r] != 1 - colors[r + 1]:
                        l = r
                        r += 1
                    else:
                        l += 1
                        r += 1
                else:
                    r += 1

        return count
