    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int c=0;
        bool f=false;
        // for(int i=0;i<n;i++)
        // {
        //     if(a(colors,i,k,n))
        //     {
        //         c++;
        //     }
        // }
        // return c;
        if(k==1)
        {
            return n;
        }
        int x=1;
        int i=1;
        while(i+1<n+k)
        {
            int cc=i;
            int p=i-1;
            if(cc>=n)
            {
                cc=cc-n;
                //p=p-n;
            }
            if(p>=n)
            {
                p=p-n;
            }
            if(colors[cc]!=colors[p])
            {
                x++;
            }
            else
            {
                x=1;
            }
            if(x>=k)
            {
                c++;
            }
            i++;
        }
        return c;
    }
};
