class Solution {
    public int numberOfAlternatingGroups(int[] c, int k) {
       int n = c.length;

    // Efficiently append elements using System.arraycopy
    int[] arr = new int[n + k - 1];
    System.arraycopy(c, 0, arr, 0, n);
    System.arraycopy(c, 0, arr, n, k - 1);

    int res = 0;
    int start = 1;

    for (int i = 1; i < arr.length; i++) {
        start =  (arr[i]!=arr[i-1]) ? start+1 : 1;
        if(start>=k)res++;
    }


    return res;
    }
}
