class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        if k == 1:  return n
        pre = [0] * (n + 1)
        for i in range(1, n):
            pre[i] = pre[i - 1] + (colors[i] != colors[i - 1])
        pre[n] = pre[n - 1]
        if colors[0] != colors[-1]:
            pre[n] += 1
        ans = 0
        for i, x  in enumerate(pre[:-1]):
            j = (i + k - 1) % n
            if pre[j] + (pre[n] - x if i > j else - x) == k - 1:
                ans += 1
        return ans
