class Solution {
    public int numberOfAlternatingGroups(int[] arr, int k) {
        int num_found = 0;
        int ikl = 0;
        int jmn = 1;

        while (jmn < arr.length + k && ikl < arr.length) {
            int temp_j = jmn;
            int temp_end = temp_j % arr.length;

            while (arr[temp_end] != arr[(temp_end - 1 + arr.length) % arr.length] && (temp_j < (arr.length + k - 1))) {
                temp_j++;
                temp_end = temp_j % arr.length;
            }

            num_found += Math.max(temp_j - ikl - k + 1, 0);
            ikl = temp_j;
            jmn = ikl + 1;
        }

        return num_found;
    }
}
