const int N = 2E5 + 10;
class Solution {
    int n, a[N];
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for (int i = 1; i <= n; i++) {
            a[i] = colors[i - 1];
        }
        for (int i = n + 1; i <= 2 * n; i++) {
            a[i] = a[i - n];
        }
        int ans = 0;
        for (int l = 1, r = 2; l <= n;) {
            if (r - l + 1 == k && a[r] != a[r - 1]) {
                ans++;
                l++, r++;
                continue;
            }
            while (r - l + 1 < k && a[r] != a[r - 1]) {
                r++;
            }
            if (r - l + 1 < k) {
                l = r;
                r++;
            } else {
                if (a[r] != a[r - 1]) {
                    ans++;
                    l++, r++;
                } else {
                    l = r;
                    r++;
                }
            }
        }
        return ans;
    }
};
