class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        cnt = [0] * n
        ans = 0
        for i in range(n + k - 1):
            i %= n
            if colors[i] != colors[i - 1]:
                cnt[i] = cnt[i - 1] + 1
            else:
                cnt[i] = 1
            if cnt[i] >= k:
                ans += 1
        return ans
