class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i = 0; i < n; i++){
            colors.push_back(colors[i]);
        }


        set<int> bad;
        for(int i = 1; i < 2 * n; i++){
            if(colors[i] == colors[i - 1]){
                bad.insert(i);
            }
        }
        bad.insert(2 * n);
        int ans = 0;
        for(int i = 0; i < n; i++){
            int num = (*bad.upper_bound(i));
            if(num >= i + k)
                ans++;
        }
        return ans;
    }
};
