class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
         int ct=0;
         vector<int>b=a;
         for(int i=0;i<k;i++)b.push_back(a[i]);
        int n=b.size();
        vector<int>p(n+1,0);
        for(int i=0;i<n;i++){
            if(i){
                p[i]+=p[i-1]+abs(b[i]-b[i-1]);
            }
        }
        for(int i=k;i<n;i++)if(p[i]-p[i-k+1]+1==k)ct++;
        return ct;

    }
};
