class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {


       int n = colors.size();
        int count = 0;
        int alternating = 0;

        for (int i = 1; i < k; i++) {
            if (colors[i] != colors[i-1]) alternating++;
        }

        for (int i = 0; i < n; i++) {
            if (alternating == k-1) count++;

            if (colors[(i+1) % n] != colors[i]) alternating--;

            if (colors[(i+k) % n] != colors[(i+k-1) % n]) alternating++;
        }

        return count;

    }
};
