class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int len = colors.length;
        int res = 0;
        int j = 0;
        for(int i=0; j<len; ++i) {
            if(i-j+1 == k) {
                res++;
                j++;
            }
            if(colors[i%len] == colors[(i+1)%len]) {
                j = i+1;
            }
        }
        return res;
    }
}
