class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int count = 0;
        int i = 0;
        int j = 1;

        while (j < n + k && i < n) {
            int temp1 = j;
            int temp2 = j % n;
            while (colors[temp2] != colors[(temp2 - 1 + n) % n] && (temp1 < (n + k - 1))) {
                temp1++;
                temp2 = temp1 % n;
            }
            int curr=temp1 - i - k + 1;
            count += max(curr, 0);
            j = i + 1;
            i = temp1;
        }

        return count;
    }
};
