class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int total = 0;
        int count = 0;
        int n = colors.size();
        for (int i = 1; i + 1 < k; i++) {
            if (colors[i] != colors[i - 1]) count++;
        }
        for (int l = 0, r = k - 1; l < n; l++, r = (r+1) % n) {
            if (colors[r] != colors[(r - 1 + n) % n]) count++;
            if (count == k - 1) total++;
            if (colors[l] != colors[(l + 1) % n]) count--;
        }
        return total;
    }
};
