class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if (n < k) return 0;

        int count = 0;
        int temp = 0;

        for (int i = 0; i < k - 1; ++i) {
            if (colors[i] != colors[i + 1]) temp++;
        }

        if (temp == k - 1) count++;

        for (int i = 1; i < n; ++i) {
            if (colors[(i - 1) % n] != colors[(i - 1 + 1) % n]) temp--;

            if (colors[(i + k - 2) % n] != colors[(i + k - 1) % n]) temp++;

            if (temp == k - 1) count++;
        }

        return count;
    }
};
