class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        // vector<int> nums = colors;
        int n = colors.size();

        for(int i = 0; i < n; i++){
            colors.push_back(colors[i]);
        }

        int left = 0, right = 1;
        int count = 0;
        bool flag=0;
        while(left < n){
            while(left < n && right - left + 1 < k){
                if(colors[right] != colors[right-1]){
                    right++;
                }
                else{
                    left = right;
                    right++;
                }
            }
            if(left >= n){
                flag=1;
            }
            if(flag)break;
            if(colors[right] != colors[right-1]){
                count++;
                left++;
                right++;
            }
            else{
                left = right;
                right++;
            }
        }

        return count;
    }
};
