#define ll long long
class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {
        int n = col.size();
        for(int i = 0; i < k-1; i++){
            col.push_back(col[i]);
        }
        int ans = 0;
        for(ll i = 0; i < n; i++){
            ll sz = 1;
            ll cur = col[i];
            i++;
            while(i < n+k-1 && cur != col[i]){
                sz++;
                cur = col[i];
                i++;
            }
            i--;
            if(sz >= k){
                ans += sz-k+1;
            }
        }

        return ans;
    }
};
