class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(), ans = 0, cur = colors[0] ^ 1;
        int m = n + k - 1, cnt = 0;
        for (int i = 0; i < m; ++i) {
            if ((cur ^ 1) == colors[i % n]) {
                ++cnt;
                cur ^= 1;
                if (cnt >= k)
                    ++ans;
            } else {
                cur = colors[i % n];
                cnt = 1;
            }
        }
        return ans;
    }
};
