class Solution 
{
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) 
    {
        int n = colors.size();
        int count = 0;

        for (int i = 0; i < n; i++)
        {
            int temp = colors[i];
            colors.push_back(temp);
        }

        int head = 0;
        int tail = 0;
        while (head < n)
        {
            while (head < n)
            {
                if (tail - head + 1 == k)
                {
                    count++;
                    break;
                }

                if (colors[tail] == colors[tail + 1])
                {
                    head = tail + 1;
                    tail = head;
                }
                else
                {
                    tail++;
                }
            }

            if (count == 0 || head >= n)
                break;

            while (head < n - 1)
            {
                if (colors[tail] != colors[tail + 1])
                {
                    head++;
                    tail++;
                    count++;
                }
                else
                    break;
            }

            head = tail + 1;
            tail = head;
        }
        return count;
    }
};
