class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int ans=0;
        for(int i=0; i<k-1; i++)
            a.push_back(a[i]);

        int i=0, j=1;
        while(j<a.size()){
            if(a[j] != a[j-1]){
                if(j-i+1 >= k)
                    ans++;
            }else{
                i=j;
            }
            j++;
        }
        return ans;

    }
};
