class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> v(colors);
        for (int a : colors)
            v.push_back(a);
        int r = 0,l=0,ans=0;
        deque<int> q;
        for (int i = 0; i < k; i++) {
            if(q.empty()||q.back()!=v[i])
            q.push_back(v[i]);
            else
            {
                q.clear();
                q.push_back(v[i]);
                l=i;
            }
            r++;
        }
        for (; l < n; l++) {
            while(r<2*n&&r-l<k){
                if(q.back()!=v[r]){
                    q.push_back(v[r]);
                    r++;
                }else{
                    q.clear();
                    q.push_back(v[r]);
                    l=r;
                    r++;
                    break;
                }
            }
            if(r-l==k)ans++;
            if(l==r-1)l--;
        }
        return ans;
    }
};
